link.script:
"use strict";
// Leave warning (kept as requested)
window.onbeforeunload = function (e) {
  const msg = "Are you sure you want to leave? Your session may be lost.";
  e = e || window.event;
  if (e) e.returnValue = msg;
  return msg;
};

